import React, { useState } from 'react';
import './PetForm.css';

const PetForm = ({ onPetRegistered }) => {
  const [pet, setPet] = useState({
    name: '',
    breed: '',
    age: '',
    bio: '',
    photo: null,
    photoPreview: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPet({ ...pet, [name]: value });
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPet({
          ...pet,
          photo: file,
          photoPreview: reader.result
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Pass the complete pet data to parent component
    if (typeof onPetRegistered === 'function') {
      onPetRegistered(pet);
    }
    // Clear form after submission (optional)
    setPet({
      name: '',
      breed: '',
      age: '',
      bio: '',
      photo: null,
      photoPreview: ''
    });
  };

  return (
    <div className="pet-form-container">
      <h2>Create Pet Profile</h2>
      <form onSubmit={handleSubmit}>
        {/* Photo Upload with Preview */}
        <div className="form-group photo-upload">
          <label>
            Profile Photo*
            <div className="photo-preview">
              {pet.photoPreview ? (
                <img src={pet.photoPreview} alt="Pet preview" />
              ) : (
                <div className="photo-placeholder">No photo selected</div>
              )}
            </div>
            <input
              type="file"
              accept="image/*"
              onChange={handlePhotoChange}
              required
            />
          </label>
        </div>

        {/* Pet Name */}
        <div className="form-group">
          <label>Pet Name*</label>
          <input
            type="text"
            name="name"
            value={pet.name}
            onChange={handleChange}
            placeholder="e.g. Max"
            required
          />
        </div>

        {/* Breed */}
        <div className="form-group">
          <label>Breed*</label>
          <input
            type="text"
            name="breed"
            value={pet.breed}
            onChange={handleChange}
            placeholder="e.g. Golden Retriever"
            required
          />
        </div>

        {/* Age */}
        <div className="form-group">
          <label>Age*</label>
          <input
            type="number"
            name="age"
            min="0"
            max="30"
            value={pet.age}
            onChange={handleChange}
            placeholder="Years"
            required
          />
        </div>

        {/* Bio */}
        <div className="form-group">
          <label>Short Bio</label>
          <textarea
            name="bio"
            value={pet.bio}
            onChange={handleChange}
            placeholder="Tell us about your pet's personality, habits..."
            rows="3"
          />
        </div>

        <button type="submit" className="submit-btn">
          Register Pet
        </button>
      </form>
    </div>
  );
};

export default PetForm;
