import React, { useState, useRef, useEffect } from "react";
import { Link } from "react-router-dom";
import { useAuth } from "./context/AuthContext";  // Adjust path if needed
import "./Navbar.css";

const Navbar = () => {
  const { currentUser, logout } = useAuth();
  const [showProfile, setShowProfile] = useState(false);
  const profileRef = useRef();

  async function handleLogout() {
    try {
      await logout();
      setShowProfile(false);
    } catch (err) {
      console.error("Logout failed:", err);
    }
  }

  // Close dropdown if click outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (profileRef.current && !profileRef.current.contains(event.target)) {
        setShowProfile(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  function getInitials() {
    if (!currentUser) return "";
    const name = currentUser.displayName || "";
    if (name) {
      const parts = name.split(" ");
      if (parts.length === 1) return parts[0].charAt(0).toUpperCase();
      return (parts[0].charAt(0) + parts[1].charAt(0)).toUpperCase();
    }
    return currentUser.email.charAt(0).toUpperCase();
  }

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">
          MyPet üêæ
        </Link>

        <div className="menu-icon">
          <span className="menu-bar"></span>
          <span className="menu-bar"></span>
          <span className="menu-bar"></span>
        </div>

        <ul className="nav-menu">
          <li className="nav-item">
            <Link to="/" className="nav-links">
              Home
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/register-pet" className="nav-links">
              Register Pet
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/my-pets" className="nav-links">
              My Pets
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/generate-qr" className="nav-links">
              Generate QR
            </Link>
          </li>

          {!currentUser && (
            <>
              <li className="nav-item">
                <Link to="/login" className="nav-links">
                  Login
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/signup" className="nav-links">
                  Sign Up
                </Link>
              </li>
            </>
          )}

          {currentUser && (
            <li className="nav-item" ref={profileRef} style={{ position: "relative" }}>
              <button
                onClick={() => setShowProfile((prev) => !prev)}
                className="profile-button"
                aria-label="User Profile"
                style={{
                  width: "40px",
                  height: "40px",
                  borderRadius: "50%",
                  border: "none",
                  backgroundColor: "#555",
                  color: "white",
                  fontWeight: "bold",
                  fontSize: "1rem",
                  cursor: "pointer",
                }}
              >
                {getInitials()}
              </button>

              {showProfile && (
                <div
                  className="profile-dropdown"
                  style={{
                    position: "absolute",
                    top: "50px",
                    right: 0,
                    backgroundColor: "white",
                    color: "black",
                    padding: "1rem",
                    borderRadius: "8px",
                    boxShadow: "0 4px 8px rgba(0,0,0,0.2)",
                    minWidth: "220px",
                    zIndex: 10,
                  }}
                >
                  <p>
                    <strong>Name:</strong> {currentUser.displayName || "N/A"}
                  </p>
                  <p>
                    <strong>Phone:</strong> {currentUser.phone || "N/A"}
                  </p>
                  <p>
                    <strong>Email:</strong> {currentUser.email}
                  </p>
                  <button
                    onClick={handleLogout}
                    style={{
                      marginTop: "0.5rem",
                      backgroundColor: "#e53935",
                      border: "none",
                      color: "white",
                      padding: "0.5rem 1rem",
                      cursor: "pointer",
                      borderRadius: "4px",
                      width: "100%",
                    }}
                  >
                    Logout
                  </button>
                </div>
              )}
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
