// src/components/MyPets/MyPets.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MyPets.css';

const MyPets = ({ userId }) => {
  const [pets, setPets] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!userId) return;
    axios.get(`http://192.168.1.58:4000/api/v1/mypets/${userId}`)
      .then(res => {
        if (res.data.success) {
          setPets(res.data.pets);
        } else {
          setError('No pets found.');
        }
      })
      .catch(() => setError('Error fetching pets.'));
  }, [userId]);

  if (error) return <p>{error}</p>;
  if (!pets.length) return <p>Loading your pets...</p>;

  return (
    <div className="my-pets-container">
      <h2>My Registered Pets</h2>
      <div className="pet-list">
        {pets.map(pet => (
          <div key={pet._id} className="pet-card">
            <img src={`http://192.168.1.58:4000/${pet.profilePhoto}`} alt={pet.name} />
            <h3>{pet.name}</h3>
            <p><strong>Breed:</strong> {pet.breed}</p>
            <p><strong>Age:</strong> {pet.age} years</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyPets;
