import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';

const GenerateQR = () => {
  const { state } = useLocation();
  const [pet, setPet] = useState(state?.pet || {});
  const [owner, setOwner] = useState({
    phone: '',
    email: ''
  });
  const [qrValue, setQrValue] = useState('');

  useEffect(() => {
    if (owner.phone || owner.email) {
      setQrValue(
        `PET:${pet.name}|OWNER:${owner.phone},${owner.email}|AGE:${pet.age}`
      );
    }
  }, [owner, pet]);

  const handleDownload = () => {
    const svg = document.querySelector("svg");
    const svgData = new XMLSerializer().serializeToString(svg);
    const link = document.createElement("a");
    link.href = `data:image/svg+xml;base64,${btoa(svgData)}`;
    link.download = `mypet-qr-${pet.name}.svg`;
    link.click();
  };

  return (
    <div className="qr-container">
      <h2>Generate Pet QR</h2>
      <div className="qr-form">
        {/* Owner Contact */}
        <div className="form-group">
          <label>Your Phone Number*</label>
          <input
            type="tel"
            value={owner.phone}
            onChange={(e) => setOwner({...owner, phone: e.target.value})}
            placeholder="+1 234 567 8900"
            required
          />
        </div>

        <div className="form-group">
          <label>Your Email*</label>
          <input
            type="email"
            value={owner.email}
            onChange={(e) => setOwner({...owner, email: e.target.value})}
            placeholder="owner@example.com"
            required
          />
        </div>

        {/* QR Preview */}
        {qrValue && (
          <div className="qr-preview">
            <QRCodeSVG 
              value={qrValue} 
              size={200}
              fgColor="#4b9cd3"
              includeMargin={true}
            />
            <button onClick={handleDownload} className="download-btn">
              Download QR
            </button>
            <p className="qr-scans-to">
              <strong>Scans to:</strong> Shows "{pet.name}'s details + your contact
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default GenerateQR;
